function esicPreloadImages(array){if(!esicPreloadImages.list){esicPreloadImages.list=[];}var list=esicPreloadImages.list;for(var i=0;i<array.length;i++){var img=new Image();img.onload=function(){var index=list.indexOf(this);if(index!==-1){list.splice(index,1);}};list.push(img);img.src=array[i];}}function FindAllImagesSrc(){var imgs=document.getElementsByTagName("img");var imgSrcs=[];for(var i=0;i<imgs.length;i++){imgSrcs.push(imgs[i].src);}return imgSrcs;}jQuery(document).ready(function($){var allImages=FindAllImagesSrc();esicPreloadImages(allImages);});

jQuery(document).on('click','.leftsidebar li',function(){
    var url = $(this).find('a').attr("href");
    start   = url.lastIndexOf('#');
    urlid = url.substring(start+1);
    $("body, html").animate({
        scrollTop: $("#"+urlid).offset().top-70,
    }, 3000 );
});
